import React, { useState } from 'react';
import { scanForVulnerabilities } from './api';
import './Scanner.css'; // Import the CSS file

const Scanner = () => {
    const [url, setUrl] = useState('');
    const [scanType, setScanType] = useState('sql_injection');
    const [result, setResult] = useState(null);

    const handleScan = async () => {
        const data = await scanForVulnerabilities(url, scanType);
        setResult(data);
    };

    return (
        <div className="scanner-container">
            <h2>Vulnerability Scanner</h2>
            <div className="scanner-input-group">
                <label>Target URL:</label>
                <input
                    type="text"
                    value={url}
                    onChange={(e) => setUrl(e.target.value)}
                    placeholder="Enter URL"
                />
            </div>
            <div className="scanner-input-group">
                <label>Scan Type:</label>
                <select value={scanType} onChange={(e) => setScanType(e.target.value)}>
                    <option value="sql_injection">SQL Injection</option>
                    <option value="command_injection">Command Injection</option>
                </select>
            </div>
            <button onClick={handleScan} className="scanner-button">Start Scan</button>
            {result && (
                <div className="scanner-result">
                    {result.error ? (
                        <p className="error">Error: {result.error}</p>
                    ) : (
                        <p>Vulnerability Status: {result.vulnerable ? "Vulnerable" : "Not Vulnerable"}</p>
                    )}
                </div>
            )}
        </div>
    );
};

export default Scanner;
